// Basic test for Skip()
func TestSkip(t *testing.T) {
	const bufferSize = 1024
	const windowSize = 16
	const title = "TestSkip"

	t.Logf("start %s", title)
	hb, err := NewHashBuffer("./testdata/data_17", bufferSize, windowSize)
	check(t, err)
	hb.SetTesting(t)
	defer func() {
		t.Log("Closing")
		err := hb.Close()
		check(t, err)
	}()
	skipped, err := hb.Skip(1)
	check(t, err)
	if skipped != 1 {
		t.Errorf("Error TestSkip: skipped %d, should have skipped 1", skipped)
	}
	testGet(t, hb, title, testData, 1)
	testGetZero(t, hb, title)
}

